import { confirmAlert } from 'react-confirm-alert'; // Import
import React, { Component } from 'react'
import 'react-confirm-alert/src/react-confirm-alert.css' // Import css

class confirm extends Component {
  constructor(props){
    super(props);
    this.handleSave = this.handeleSave.bind(this);
    this.state = {}
  }
  usernameHandler(e) {
    this.setState({username:e.target.value});
  };
  handleSave() {
    const item = this.state;
    this.props.Save(item)
}


    submit = (a) => {
        confirmAlert({
          message: 'Suppression Produit',
          buttons: [
            {
              label: 'Oui',
              onClick: () => this.props.deleteUser(a)
            },
            {
              label: 'Non',
            }
          ],
        })
      };
      custom = (a) => {
        confirmAlert({

          customUI: ({ onClose }) => {

            return (
              <div className='custom-ui'>
              <input value={this.state.username} onchange={(e)=>this.usernameHandler(e)}/> <br/>
                <button onClick={()=>{this.handleSave() }}>ok</button>
                <button onClick={onClose}>Annuler</button>
              </div>
            )
          }
        })
      };

      render(){
            return (
                <center>
                    <table class="table table-bordered">
                    <thead>
                        <tr className="tab">
                            <th>id</th>
                            <th>Produits</th>
                            <th>Prix</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.props.users.length >= 0 ? (
                        this.props.users.map(user => (
                                <tr key={user.id}>
                                    <td className="cent">{user.id}</td>
                                    <td id="tex">{user.name}</td>
                                    <td id="plus"><div className="flot">{parseInt(user.username)}</div></td>
                                    <td className="cent">
                                        <button className="btn btn-danger" onClick={()=>this.submit(user.id)}>X</button>     
                                        <button className="btn btn-success" onClick={()=>this.custom(user.username)}>Edit</button>
                                    </td>
                                </tr>
                            ))
                        )
                         : (
                            <tr>
                                <td colSpan={3}></td>
                            </tr>

                        )}

                    </tbody>
                </table>
                <form 
                onSubmit={event =>{
                  event.preventDefault()
                  if(user.id+2){
                      var onemember = document.getElementById("plus");
                      var twomember = document.getElementById("two");
                      var result = document.getElementById("resultat");

                      inputone.innerHTML = "";
                          sommeerreur.innerHTML = "";
                          var addition = parseInt(onemember.value) + parseInt(twomember.value);
                          result.innerHTML = addition;
                  }

              }
              }
                >
                  <div  id="To">
                  <button className="btn btn-info btn-md" onClick="somme()">TOTAL</button>
                  </div>
                  <div>
                  <h4 id="resultat">TOTAL :</h4>
                  </div>
                </form>     

	</center>
            );
      }
}


export default confirm;
