import { confirmAlert } from 'react-confirm-alert'; // Import
import React, { Component } from 'react'
import 'react-confirm-alert/src/react-confirm-alert.css' // Import css
import Popup from '../edit/EditUserForm'

class confirm extends Component {
  constructor() {
    super();
    this.state = {
      showPopup: false
    };
  }
  togglePopup() {
    this.setState({
      showPopup: !this.state.showPopup
    });
  }

    submit = (a) => {
        confirmAlert({
          title: 'Suppression Produit',
          message: "user.name",
          buttons: [
            {
              label: 'Oui',
              onClick: () => this.props.deleteUser(a)
            },
            {
              label: 'Non',
            }
          ],
        })
      };
      custom = (a) => {
        confirmAlert({

          customUI: ({ onClose }) => {
            var edit = document.getElementById('tex')
				    edit.innerHTML = ''
            return (
              <div className='custom-ui'>
                  <Popup />
                 <button onClick={onClose}>Annuler</button>
              </div>
            )
          }
        })
      };
      render(){
            return (
                <center>
                    <table class="table table-bordered">
                    <thead>
                        <tr className="tab">
                            <th>id</th>
                            <th>Produits</th>
                            <th>Prix</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.props.users.length >= 0 ? (
                        this.props.users.map(user => (
                                <tr key={user.id}>
                                    <td className="cent">{user.id}</td>
                                    <td id="tex">{user.name}</td>
                                    <td id="plus"><div className="flot">{parseInt(user.username)}</div></td>
                                    <td className="cent">
                                        <button className="btn btn-danger" onClick={()=>this.submit(user.id)}>X</button>
                                        <button onClick={()=>this.custom(user.username)} className="btn btn-success">Edit</button>                                      
                                    </td>
                                </tr>
                            ))
                        )
                         : (
                            <tr>
                                <td colSpan={3}></td>
                            </tr>

                        )}

                    </tbody>
                </table>
                  <div  id="To">
                  <button className="btn btn-info btn-md" onClick="somme()">TOTAL</button>
                  </div>
                  <div>
                  <h4 id="resultat">TOTAL :</h4>
                  </div>   

	</center>
            );
      }
}


export default confirm;
