import React, { useState } from 'react'
import Tableau from './component/Ajoute/configAlert'
import AddUserForm from './component/forms/AddUserForm';
import 'react-confirm-alert/src/react-confirm-alert.css'
import './App.css';

import './component/forms/form.css';
import './component/Ajoute/list.css';
import 'bootstrap/dist/css/bootstrap.css';



const App = () => {


  const usersData = []

  const [count, setCount] = useState(0);

  const [users, setUsers] = useState(usersData)

  const addUser = user => {
		user.id = count
		setUsers([ ...users, user ])
  }

  const deleteUser = id => {
    setUsers(users.filter(user => user.id !== id))
  }
  const initialBookState = {
    title: '',
    available: false,
  }

  const [book, setBook] = useState(initialBookState)

  const updateBook = book => {
    setBook({ title: book.title, available: book.available })
  }

const [ editing, setEditing ] = useState(false)

const initialFormState = { id: null, name: '', username: '' }

const [ currentUser, setCurrentUser ] = useState(initialFormState)

const editRow = user => {
  setEditing(true)

  setCurrentUser({ id: user.id, name: user.name, username: user.username })
}
<Tableau users={users} editRow={editRow} deleteUser={deleteUser} />

  return (
    <div className="container">
    <div className="flex-row red"></div>
      <div className="flex-row">
        <div className="flex-large">
          <AddUserForm addUser={addUser} setCount={setCount} count={count}/>
        </div>
        <div className="flex-large">
          <Tableau users={users} deleteUser={deleteUser}/>
        </div>

        <div>

        </div>
      </div>
    </div>
  )

}

export default App;
