import React, { useState } from 'react'
import Tableau from './component/Ajoute/tableau'
import AddUserForm from './component/forms/AddUserForm';
import EditUserForm from './component/edit/EditUserForm'
import 'react-confirm-alert/src/react-confirm-alert.css'
import './App.css';

import './component/forms/form.css';
import './component/Ajoute/list.css';
import './component/edit/edit.css';
import 'bootstrap/dist/css/bootstrap.css';



const App = () => {


  const usersData = []

  const [count, setCount] = useState(0);

  const [users, setUsers] = useState(usersData)

  const addUser = user => {
		user.id = count
		setUsers([ ...users, user ])
  }

  const deleteUser = id => {
    setUsers(users.filter(user => user.id !== id))
  }

const [ editing, setEditing ] = useState(false)

const initialFormState = { id: null, name: '', username: '' }

const [ currentUser, setCurrentUser ] = useState(initialFormState)

const editRow = user => {
  setEditing(true)
  setCurrentUser({ id: user.id,username: user.username })
}

const updateUser = (id, updatedUser) => {
  setEditing(false)

  setUsers(users.map(user => (user.id === id ? updatedUser : user)))
}
  const somme=()=>{
  let total=0;
  for(let i=0;i<users.length;i++){
      total=total+parseInt(users[i].Prix);
  }
  let a=[total]     
  var numberFormat = new Intl.NumberFormat("es-ES");
 var formatted = a.map(numberFormat.format);
  return formatted.join("; ")
 }

  return (
    <div className="container">
    <div className="flex-row red"></div>
      <div className="flex-row">
          <div className="popup">
            <EditUserForm
              editing={editing}
              setEditing={setEditing}
              currentUser={currentUser}
              updateUser={updateUser}
            />
        </div>
        <div className="flex-large">
          <AddUserForm addUser={addUser} setCount={setCount} count={count}/>
        </div>
        <div className="flex-large">
          <Tableau users={users} deleteUser={deleteUser} editRow={editRow}/>
        </div>
              <div  id="To">
              <button className="btn btn-info btn-md" onClick={somme()}>TOTAL</button>
               </div>
               <div>
                <h4 >TOTAL : <span id="resultat"> </span></h4>
                </div>   
        <div>

        </div>
      </div>
    </div>
  )

}

export default App;
